import React from 'react'
import Square from "./Square";

export default class Board extends React.Component {

    render() {

        const winner = this.calcWinner(this.state.squares);

        let status;
        let substatus;

        if (winner) {
            status = 'Winner:';
            substatus = winner;
        } else {
            status = 'Next Player: ';
            substatus = this.state.nextPlayerX ? 'X' : 'O';
        }

        return (
            <React.Fragment>
                <div className="status">
                    <span> {status} </span>
                </div>
                <div className="status">
                    <span> {substatus} </span>
                </div>
                <div className="board">
                    <div className="board-row">
                        {this.renderSquare(0)}
                        {this.renderSquare(1)}
                        {this.renderSquare(2)}
                    </div>
                    <div className="board-row">
                        {this.renderSquare(3)}
                        {this.renderSquare(4)}
                        {this.renderSquare(5)}
                    </div>
                    <div className="board-row">
                        {this.renderSquare(6)}
                        {this.renderSquare(7)}
                        {this.renderSquare(8)}
                    </div>
                </div>
                <div className="scene">
                    <div className="cube" onClick={ () => this.handleRefresh() }>
                        <span className="side top"> Clear? </span>
                        <span className="side front"> Refresh </span>
                    </div>
                </div>
            </React.Fragment>
        )
    }
}
