import React from 'react'
import Square from "./Square";

export default class Board extends React.Component {

    constructor(props) {
        super(props)
        this.state = {
            squares: Array(9).fill(null),
            nextPlayerX: true
        }
    }

    handleClick(i) {
        const squares = this.state.squares.slice();
        squares[i] = this.state.nextPlayerX ? 'X' : 'O';
        this.setState({ squares: squares, nextPlayerX: !this.state.nextPlayerX })
    }

    renderSquare(i) {
        return (
            <Square
                value={ this.state.squares[i] }
                onClick={ () => this.handleClick(i) }
            />
        )
    }

    render() {
        const status = 'Next Player: ' + this.state.nextPlayerX;

        return (
            <React.Fragment>
                <div className="status">
                    {status}
                </div>
                <div className="board">
                    <div className="board-row">
                        {this.renderSquare(0)}
                        {this.renderSquare(1)}
                        {this.renderSquare(2)}
                    </div>
                    <div className="board-row">
                        {this.renderSquare(4)}
                        {this.renderSquare(5)}
                        {this.renderSquare(6)}
                    </div>
                    <div className="board-row">
                        {this.renderSquare(7)}
                        {this.renderSquare(8)}
                        {this.renderSquare(9)}
                    </div>
                </div>
            </React.Fragment>
        )
    }
}
